// Simple flicker effect for the screen
function flicker() {
    const screen = document.querySelector('.screen');
    if (Math.random() < 0.05) {
        screen.style.opacity = 0.8;
    } else {
        screen.style.opacity = 1;
    }
}

setInterval(flicker, 100); 

